// src/App.jsx
import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useLocation } from 'react-router-dom';
import FraudPage from './pages/FraudPage';
import CounterfeitPage from './pages/CounterfeitPage';
import ReviewSpamPage from './pages/ReviewSpamPage';
import ReturnAnomalyPage from './pages/ReturnAnomalyPage';
import { Eye } from 'lucide-react';

// Apply favicon and branding similar to Amazon
const faviconLink = document.createElement('link');
faviconLink.rel = 'icon';
faviconLink.href = 'https://www.amazon.com/favicon.ico';
document.head.appendChild(faviconLink);

document.title = 'Amazon Security Console';

document.documentElement.classList.add('text-sm', 'text-black');
document.body.classList.add('bg-white', 'font-sans');

const sampleAlert = {
  id: 'a1',
  title: 'Suspicious Transaction',
  riskLevel: 'High',
  description: 'Detected anomaly in refund pattern.',
  investigationBrief: 'Customer refunded multiple orders within 24hrs.',
  riskIndicators: ['multi_refund', 'abnormal_timing'],
  subAgentResults: {
    PatternAnalyzer: { score: 85, confidence: 'High' },
    BehaviorProfiler: { score: 70, confidence: 'Medium' },
  },
};

const Sidebar = () => {
  const location = useLocation();
  const navItems = [
    { to: '/', label: 'Fraud' },
    { to: '/counterfeit', label: 'Counterfeit' },
    { to: '/review', label: 'Review Spam' },
    { to: '/return', label: 'Return Anomaly' },
  ];

  return (
    <aside className="w-64 bg-white border-r shadow-md">
      <div className="p-4 text-xl font-bold text-yellow-500 border-b">Amazon Security Console</div>
      <nav className="flex flex-col space-y-2 p-4 text-sm">
        {navItems.map(({ to, label }) => (
          <Link
            key={to}
            to={to}
            className={`block py-1 ${location.pathname === to ? 'text-yellow-600 font-semibold' : 'text-black hover:text-yellow-500'}`}
          >
            {label}
          </Link>
        ))}
      </nav>
    </aside>
  );
};

const InvestigationPanel = ({ selectedAlert, handleAction }) => (
  <div className="bg-white rounded-lg shadow">
    <div className="px-6 py-4 border-b border-gray-200">
      <h2 className="text-lg font-semibold text-gray-900">Investigation Details</h2>
    </div>
    {selectedAlert ? (
      <div className="p-6">
        <div className="mb-6">
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-lg font-medium text-gray-900">{selectedAlert.title}</h3>
            <span className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-medium border ${getRiskColor(selectedAlert.riskLevel)}`}>
              {selectedAlert.riskLevel}
            </span>
          </div>
          <p className="text-sm text-gray-600 mb-4">{selectedAlert.description}</p>

          <div className="bg-gray-50 rounded-lg p-4 mb-4">
            <h4 className="text-sm font-medium text-gray-900 mb-2">Investigation Brief</h4>
            <p className="text-sm text-gray-700">{selectedAlert.investigationBrief}</p>
          </div>

          <div className="mb-4">
            <h4 className="text-sm font-medium text-gray-900 mb-2">Risk Indicators</h4>
            <div className="flex flex-wrap gap-2">
              {selectedAlert.riskIndicators.map((indicator, index) => (
                <span key={index} className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-red-100 text-red-800">
                  {indicator.replace('_', ' ')}
                </span>
              ))}
            </div>
          </div>

          <div className="mb-6">
            <h4 className="text-sm font-medium text-gray-900 mb-2">Sub-Agent Analysis</h4>
            <div className="space-y-2">
              {Object.entries(selectedAlert.subAgentResults).map(([agent, result]) => (
                <div key={agent} className="flex items-center justify-between p-3 bg-gray-50 rounded">
                  <span className="text-sm font-medium text-gray-700">{agent.replace(/([A-Z])/g, ' $1')}</span>
                  <div className="text-right">
                    <div className="text-sm font-medium text-gray-900">Score: {result.score}</div>
                    <div className="text-xs text-gray-600">Confidence: {result.confidence}</div>
                  </div>
                </div>
              ))}
            </div>
          </div>

          <div className="flex space-x-3">
            <button onClick={() => handleAction(selectedAlert.id, 'confirmed_fraud')} className="flex-1 bg-red-600 text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-red-700 transition-colors">
              Confirm Fraud
            </button>
            <button onClick={() => handleAction(selectedAlert.id, 'false_positive')} className="flex-1 bg-gray-600 text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-gray-700 transition-colors">
              False Positive
            </button>
            <button onClick={() => handleAction(selectedAlert.id, 'escalate')} className="flex-1 bg-orange-600 text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-orange-700 transition-colors">
              Escalate to Legal
            </button>
          </div>
        </div>
      </div>
    ) : (
      <div className="p-6 text-center text-gray-500">
        <Eye className="h-12 w-12 mx-auto mb-4 text-gray-300" />
        <p>Select an alert to view investigation details</p>
      </div>
    )}
  </div>
);

const getRiskColor = (riskLevel) => {
  switch (riskLevel) {
    case 'High': return 'bg-red-100 text-red-800 border-red-300';
    case 'Medium': return 'bg-yellow-100 text-yellow-800 border-yellow-300';
    case 'Low': return 'bg-green-100 text-green-800 border-green-300';
    default: return 'bg-gray-100 text-gray-800 border-gray-300';
  }
};

const App = () => {
  const selectedAlert = sampleAlert;

  const handleAction = (id, actionType) => {
    console.log(`Alert ${id} marked as: ${actionType}`);
  };

  return (
    <Router>
      <div className="flex min-h-screen bg-gray-100">
        <Sidebar />
        <main className="flex-1 p-6 space-y-6">
          <Routes>
            <Route path="/" element={<FraudPage />} />
            <Route path="/counterfeit" element={<CounterfeitPage />} />
            <Route path="/review" element={<ReviewSpamPage />} />
            <Route path="/return" element={<ReturnAnomalyPage />} />
          </Routes>
          <InvestigationPanel selectedAlert={selectedAlert} handleAction={handleAction} />
        </main>
      </div>
    </Router>
  );
};

export default App;
